/* Extracting index panel */ 
drop table if exists #memb_list_mm 
select distinct 
	MBI, 
	eligibility_month, 
	ckcc_id, 
	ckcc_market
into 
	#memb_list_mm 
from 
	FreseniusBusinessData.DBO.CKCC_ELIGIBILITY 
WHERE 
	MM = 1 and 
	NOT (PY = 0 AND YEAR(CKCC_ACTIVATION_DATE) >= 2023) and 
	FHP_ALIGN_TYPE = 'CKD' and 
	eligibility_month between '2022-01-01' and '2023-12-01' and 
	DROPPED_PRACTICE_MM_CKD <> 1

/* extracting index panel (only members) */ 
drop table if exists #memb_list 
select distinct 
	mbi 
into 
	#memb_list 
from
	#memb_list_mm 

/* identifying earliest part-a chf diagnoses  */ 
drop table if exists #earliest_parta_diag 
; with parta_chfs as (
	select 
		a.MBI, 
		b.* 
	from 
		#memb_list a 
	left join (
		select 
			bene_mbi_id, 
			cur_clm_uniq_id, 
			clm_dgns_cd, 			
			clm_from_dt, 
			clm_thru_dt 
		from 
		fhp_ckcc.[dbo].[CKCC_Claims_CCLF4_PartA_Diagnosis_Code_Fnl]
		where 
			(clm_dgns_cd like 'I50%' or clm_dgns_cd in ('I0989', 'I110', 'I130', 'I132'))
			and
			not (substring(PRVDR_OSCAR_NUM,3,1) IN ('T','R')) OR (substring(PRVDR_OSCAR_NUM,3,4) between '3025' and '3099') AND 
			NOT (substring(PRVDR_OSCAR_NUM,3,4) between '2000' and '2299') 
	) b 
		on a.mbi = b.bene_mbi_id 	
)

select distinct 
	a.mbi, 
	cur_clm_uniq_id, 
	clm_from_dt, 
	clm_thru_dt 
into 
	#earliest_parta_diag 
from 
	parta_chfs a 
inner join (
	select 
		mbi,
		min(clm_from_dt) as earliest_diagnosis 
	from 
		parta_chfs 
	group by 
		mbi 
) b 
	on a.mbi = b.mbi and 
		a.clm_from_dt = b.earliest_diagnosis 


/* attributing eligiblity in 2022-23 and inpatient status of diagnosis */
drop table if exists #parta_chfs
select distinct 
	b.mbi,
	a.clm_from_dt, 
	max(a.clm_thru_dt) as clm_thru_dt, /* handling overlapping discharge dates by max */ 
	max(case when c.clm_bill_fac_type_cd in (1, 4) and c.CLM_BILL_CLSFCTN_CD = 1 then 1 else 0 end) as inpatient_ind
into 
	#parta_chfs 
from 
	#earliest_parta_diag a
inner join
	#memb_list_mm b 
		on a.mbi = b.mbi and 
			left(a.clm_from_dt, 7) = left(b.eligibility_month, 7) 
left join (
	select 
		* 
	from 
		fhp_ckcc.[dbo].[CKCC_Claims_CCLF1_PartA_Claims_Header_fnl] 
	where 
		final_row = 1 

		-----------------ltan: adding ip hospital bill type 
		
	     and ((CLM_BILL_FAC_TYPE_CD = 1 AND CLM_BILL_CLSFCTN_CD = 1) /*INPATIENT HOSPITAL, bill types 111-118*/
	           OR (CLM_BILL_FAC_TYPE_CD = 4 AND CLM_BILL_CLSFCTN_CD = 1
			    ))
) c 
		on a.mbi = c.BENE_MBI_ID and 
			a.cur_clm_uniq_id = c.cur_clm_uniq_id 
group by 
	b.mbi, 
	a.clm_from_dt
	
/* part B chfs - requiring an chf inpatient within 30days of outpatient visit */ 
drop table if exists #earliest_partb_diag 
; with partb_chfs as (
	select 
		a.mbi, 
		clm_from_dt, 
		clm_thru_dt, 
		clm_pos_cd 
	from 
		#memb_list a 
	left join (
		select 
			bene_mbi_id, 
			clm_from_dt, 
			clm_thru_dt, 
			clm_pos_cd 
		from 
		 	fhp_ckcc.[dbo].[CKCC_Claims_CCLF5_PartB_Physicians_fnl] 
		where 
			final_row = 1 and (
				clm_dgns_1_cd like 'I50%' or clm_dgns_1_cd in ('I0989', 'I110', 'I130', 'I132') or
				clm_dgns_2_cd like 'I50%' or clm_dgns_2_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_3_cd like 'I50%' or clm_dgns_3_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_4_cd like 'I50%' or clm_dgns_4_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_5_cd like 'I50%' or clm_dgns_5_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_6_cd like 'I50%' or clm_dgns_6_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_7_cd like 'I50%' or clm_dgns_7_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_dgns_8_cd like 'I50%' or clm_dgns_8_cd in ('I0989', 'I110', 'I130', 'I132') or 
				clm_line_dgns_cd like 'I50%' or clm_line_dgns_cd in ('I0989', 'I110', 'I130', 'I132')
		) 
	) b on 
		a.mbi = b.bene_mbi_id 
)
select 
	a.mbi, 
	a.clm_from_dt, 
	a.clm_thru_dt, 
	a.clm_pos_cd 
into 
	#earliest_partb_diag 
from 
	partb_chfs a 
inner join (
	select 
		mbi, 
		min(clm_from_dt) as earliest_diagnosis 
	from 
		partb_chfs 
	group by 
		mbi 
) b 
	on a.mbi = b.mbi and
		a.clm_from_dt = b.earliest_diagnosis 

/* attribute eligibility and 2022-23 range to outpatient diagnosis */ 
drop table if exists #partb_chfs 
select distinct 
	a.mbi, 
	clm_from_dt, 
	max(clm_thru_dt) as clm_thru_dt, /* some inpatients have multiple discharges */ 
	max(case when clm_pos_cd = 21 then 1 else 0 end) as pos_21_ind 
into 
	#partb_chfs 
from 
	#earliest_partb_diag a 
inner join 
	#memb_list_mm b 
		on a.mbi = b.mbi and 
			left(a.clm_from_dt, 7) = left(b.eligibility_month, 7) 
group by 
	a.mbi, 
	clm_from_dt 

/* pull all 30 day post diagnosis inpatients for outpatient dx members */ 
drop table if exists #op_30d_ips 
select 
	mbi, 
	a.clm_thru_dt as op_dx_date, 
	b.cur_clm_uniq_id, 
	b.clm_from_dt as admit_dt, 
	b.clm_thru_dt as discharge_dt
into 
	#op_30d_ips 
from 
	#partb_chfs a 
left join (
	select 
		a.bene_mbi_id, 
		cur_clm_uniq_id, 
		clm_from_dt, 
		clm_thru_dt
	from 
		fhp_ckcc.[dbo].[CKCC_Claims_CCLF1_PartA_Claims_Header_fnl] a
	inner join 
		#memb_list_mm b  on 
			a.bene_mbi_id = b.mbi and 
			left(b.eligibility_month, 7) = left(a.clm_from_dt, 7) 
	where
		final_row = 1 and 
		not (substring(PRVDR_OSCAR_NUM,3,1) IN ('T','R')) OR (substring(PRVDR_OSCAR_NUM,3,4) between '3025' and '3099') AND 
		NOT (substring(PRVDR_OSCAR_NUM,3,4) between '2000' and '2299') and 
	    ((CLM_BILL_FAC_TYPE_CD = 1 AND CLM_BILL_CLSFCTN_CD = 1) /*INPATIENT HOSPITAL, bill types 111-118*/
	    OR (CLM_BILL_FAC_TYPE_CD = 4 AND CLM_BILL_CLSFCTN_CD = 1)) 
) b 
	on a.mbi = b.bene_mbi_id and 
		b.clm_from_dt between a.clm_thru_dt and dateadd(day, 29, a.clm_thru_dt)  -----ltan: add 29? FIXED!!!

/* find the earliest chf inpatient within 30 day post op dx window */ -------ltan: hospital admits can be any casue, not only admits with dx chf
drop table if exists #op_30d_ips_chf 
; with all_chfs_30d_ips as (
	select 
		mbi, 
		op_dx_date, 
		b.clm_from_dt, 
		b.clm_thru_dt
	from 
		#op_30d_ips a 
	left join 
		fhp_ckcc.[dbo].[CKCC_Claims_CCLF4_PartA_Diagnosis_Code_Fnl] b 
			on a.mbi = b.bene_mbi_id and 
				a.cur_clm_uniq_id = b.cur_clm_uniq_id 
	/* where 
		clm_dgns_cd like 'I50%' or clm_dgns_cd in ('I0989', 'I110', 'I130', 'I132')  REMOVED CHF REQUIREMENT FOR 30 DAY INPATIENT FOLLOW-UP */ 
) 
select 
	a.mbi, 
	a.op_dx_date, 
	a.clm_from_dt as ip_from_dt,
	max(a.clm_thru_dt) as ip_thru_dt 
into 
	#op_30d_ips_chf 
from 
	all_chfs_30d_ips a 
inner join (
	select 
		mbi, 
		min(clm_from_dt) as earliest_ip_date 
	from 
		all_chfs_30d_ips 
	group by 
		mbi 
) b 
	on a.mbi = b.mbi and 
		a.clm_from_dt = b.earliest_ip_date 
group by 
	a.mbi, 
	a.op_dx_date, 
	a.clm_from_dt 

/* subset the outpatient diagnosis cases to those with found chf inpatients in 30 days */ 
drop table if exists #partb_chfs_final 
select distinct 
	a.mbi, 
	a.clm_thru_dt as op_dx_date, 
	a.pos_21_ind, 
	b.ip_from_dt, 
	b.ip_thru_dt 
into 
	#partb_chfs_final 
from 
	#partb_chfs a 
inner join 
	#op_30d_ips_chf b 
		on a.mbi = b.mbi and 
			a.clm_thru_dt = b.op_dx_date 


/* consolidate all part a and viable part b diagnoses */ 
drop table if exists #compiled_chfs
; with parta_diag as (
	select 
		mbi, 
		'a' as cclf, 
		clm_from_dt as diagnosis_date, 
		clm_from_dt as ip_from_dt, 
		clm_thru_dt as ip_thru_dt, 
		inpatient_ind 
	from 
		#parta_chfs 
), 

partb_diag as (
	select 
		mbi, 
		'b' as cclf, 
		op_dx_date as diagnosis_date, 
		ip_from_dt, 
		ip_thru_dt,
		pos_21_ind as inpatient_ind 
	from 
		#partb_chfs_final 
)

select 
	* 
into 
	#compiled_chfs 
from 
	parta_diag 
union all (
	select 
		* 
	from 
		partb_diag 
)	

/* identifying earliest diagnosis date and index inpatient - if tied between an cclf a and b, defer to inpatient in part a  */ 
drop table if exists #final_chf_diag 
; with earliest as (
	select 
		a.* 
	from 
		#compiled_chfs a 
	inner join (
		select 
			mbi, 
			min(diagnosis_date) as earliest_diag_date 
		from 
			#compiled_chfs 
		group by 
			mbi 
	) b 
		on a.mbi = b.mbi and 
			a.diagnosis_date = b.earliest_diag_date 
) 
select 
	a.mbi, 
	case 
		when 
			inpatient_ind = 1 
		then	
			'inpatient' 
		when 
			a.cclf = 'b' and inpatient_ind != 1 
		then 
			'outpatient' 
		else 
			'other' 
	end as diagnosis_site, 
	diagnosis_date, 
	ip_from_dt as index_ip_admit_dt, 
	ip_thru_dt as index_ip_discharge_dt
into 
	#final_chf_diag 
from 
	earliest a 
inner join (
	select 
		mbi, 
		min(cclf) as cclf
	from 
		earliest a 
	group by 
		mbi 
) b on 
	a.mbi = b.mbi and 
	a.cclf = b.cclf 

select * from #final_chf_diag 


/* identify follow-up cardiologist appointments from #final_chf_diag */ 
drop table if exists #card_30d 
select 
	a.mbi, 
	a.index_ip_discharge_dt, 
	min(b.clm_from_dt) as card_30d_visit_dt 
into 
	#card_30d 
from 
	#final_chf_diag a 
left join (
	select 
		bene_mbi_id, 
		clm_from_dt, 
		clm_thru_dt, 
		cur_clm_uniq_id 
	from 
		fhp_ckcc.[dbo].[CKCC_Claims_CCLF5_PartB_Physicians_Final]  
	where 
		clm_rndrg_fed_prvdr_spclty_cd = '06' and 
		clm_from_dt between '2022-01-01' and '2023-12-31' and ----ltan: if limit the cardio vists within 30 days of discharge, discharge will need to be truncated at the end of nov 2023; or even oct 2023 if needs 60 window for meds 
			/* TRUNCATION HAPPENS DOWN STREAM LOGIC!! */ 
		final_row = 1
	
) b on 
	a.mbi = b.bene_mbi_id and 
	b.clm_from_dt between a.index_ip_discharge_dt and dateadd(day, 29, a.index_ip_discharge_dt) ----lt: add 29? FIXED 
group by 
	mbi, 
	index_ip_discharge_dt 
	
