/* process the drugs into flags and develop 'treatment' groups' */ 
drop table if exists #drugs 
; with pharm_agg as (
	select 
		mbi,
		max(case when drug_type = 'SGLT' then 1 else 0 end) as sglt_ind, 
		max(case when drug_type = 'beta_blockers' then 1 else 0 end) as bb_ind, 
		max(case when drug_type = 'NRA' then 1 else 0 end) as nra_ind, 
		max(case when drug_type = 'ARNI' then 1 else 0 end) as arni_ind, 
		max(case when drug_type = 'ARB' then 1 else 0 end) as arb_ind, 
		max(case when drug_type = 'ACE' then 1 else 0 end) as ace_ind 
	from 
		#partd_pharm
	group by 
		mbi
) 
select 
	a.*, 
	case 
		when sglt_ind + bb_ind + nra_ind + arni_ind + arb_ind + ace_ind > 0 then 'with_drugs' 
		else 'no_drugs' 
	end as drug_treatment_group 
into 
	#drugs 
from 
	pharm_agg a

	select diagnosis_site, count(*) n from #final_chf_diag group by diagnosis_site order by n desc 

/* consolidate cardiologists and drug information to index */
drop table if exists #results 
select 
	a.mbi, 
	d.death_date, 
	d.death_month, 
	--a.ckcc_id, 
	a.diagnosis_site, 
	a.diagnosis_date, 
	DATEADD(m, DATEDIFF(m, 0, a.diagnosis_date), 0) as diagnosis_month, 
	a.index_ip_admit_dt, 
	a.index_ip_discharge_dt, 
	case when b.card_30d_visit_dt is not null then 1 else 0 end as card_30d_visit_flag, 
	b.card_30d_visit_dt,
	case 
		when sglt_ind + bb_ind + nra_ind + arni_ind + arb_ind + ace_ind > 0 then 'with_drugs' 
		else 'no_drugs' 
	end as drug_treatment_group, 
	c.sglt_ind, 
	c.bb_ind, 
	c.nra_ind, 
	c.arni_ind, 
	c.arb_ind, 
	c.ace_ind
into 
	#results 
from 
	#final_chf_diag a 
left join 
	#card_30d b 
		on a.mbi = b.mbi 
left join 
	#drugs c 
		on a.mbi = c.mbi 
left join (
	select 
		MBI, max(DEATH_DT) as death_date, max(death_month) as death_month 
	from 
		freseniusbusinessdata.dbo.CKCC_ELIGIBILITY
	group by 
		MBI 
) d
	on a.mbi = d.mbi 
