/* generate the results table appended with member month utilization */
drop table if exists #memb_months_util 
; with monthly_metric as ( 
	select 
		health_plan_id, 
		clm_from_month, 
		sum(net_paid_amt) as total_paid, 
		sum(case when irf_ltch = 'hospital' then total_admits end) as total_admits, 
		sum(case when irf_ltch = 'hospital' then total_readmits end) as total_readmits 
	from 
		freseniusBusinessData.FHP.FHP_METRICS_SUMMARY
	where 
		clm_from_month between '2022-01-01' and '2023-12-01' and 
		health_plan_id in (select distinct mbi from #results) 	
	group by 
		health_plan_id, 
		clm_from_month 
