select 
	a.mbi, 
	ckcc_id, 
	ckcc_market, 
	death_date, 
	death_month, 
	diagnosis_site, 
	diagnosis_date, 
	diagnosis_month, 
	index_ip_discharge_dt, 
	card_30d_visit_flag, 
	drug_treatment_group, 
	eligibility_month, 
	total_paid, 
	total_admits, 
	total_readmits
into 
	#memb_months_util  
from 
	#results a 
left join 
	#memb_list_mm b 
		on a.mbi = b.mbi
left join 
	monthly_metric c 
		on b.mbi = c.health_plan_id and 
			b.eligibility_month  = c.clm_from_month 
order by 
	mbi, 
	eligibility_month 

select * from #memb_months_util order by mbi, eligibility_month 

/* member-level aggregation for first three months post-discharge for weighted pmpm */ 
drop table if exists #member_level_agg 
select 
	mbi, 
	ckcc_id,
	ckcc_market, 
	death_date, 
	death_month, 
	diagnosis_site, 
	diagnosis_date, 
	diagnosis_month, 
	index_ip_discharge_dt, 
	card_30d_visit_flag, 
	drug_treatment_group, 
	count(distinct eligibility_month) as mm, 
	sum(total_paid) as total_paid, 
	sum(total_paid) / cast(count(*) as float) as pmpm, 
	sum(total_admits) as total_admits, 
	sum(total_readmits) as total_readmits 
into 
	#member_level_agg 
from 
	#memb_months_util
where 
	left(eligibility_month, 7) between left(dateadd(month, 1, index_ip_discharge_dt), 7) and left(dateadd(month, 3, index_ip_discharge_dt), 7)
group by 
	mbi, 
	ckcc_id,
	ckcc_market, 
	death_date, 
	death_month, 
	diagnosis_site, 
	diagnosis_date, 
	diagnosis_month, 
	index_ip_discharge_dt, 
	card_30d_visit_flag, 
	drug_treatment_group

select * from #member_level_agg order by mbi 


